import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import Hero from '../components/Hero';
import Section from '../components/Section';

const FilterContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-bottom: 3rem;
  flex-wrap: wrap;
`;

const FilterButton = styled.button`
  background: ${props => props.active ? 'var(--primary-color)' : 'transparent'};
  color: ${props => props.active ? 'white' : 'var(--primary-color)'};
  border: 2px solid var(--primary-color);
  padding: 0.5rem 1.5rem;
  border-radius: 25px;
  font-weight: 600;
  cursor: pointer;
  transition: var(--transition);

  &:hover {
    background: var(--primary-color);
    color: white;
  }
`;

const AircraftGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
`;

const AircraftCard = styled(motion.div)`
  background: white;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  cursor: pointer;

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
  }
`;

const AircraftImage = styled.div`
  height: 200px;
  position: relative;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.5s ease;
  }

  ${AircraftCard}:hover img {
    transform: scale(1.1);
  }
`;

const AircraftInfo = styled.div`
  padding: 1.5rem;
`;

const AircraftName = styled.h3`
  color: var(--primary-color);
  margin-bottom: 0.5rem;
`;

const AircraftPeriod = styled.p`
  color: var(--gray-color);
  font-size: 0.9rem;
  margin-bottom: 1rem;
`;

const SpecsList = styled.ul`
  list-style: none;
  padding: 0;
`;

const SpecItem = styled.li`
  display: flex;
  justify-content: space-between;
  padding: 0.5rem 0;
  border-bottom: 1px solid #eee;

  &:last-child {
    border-bottom: none;
  }
`;

const Modal = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  padding: 2rem;
`;

const ModalContent = styled(motion.div)`
  background: white;
  padding: 2rem;
  border-radius: 10px;
  max-width: 800px;
  width: 90%;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: var(--primary-color);
`;

const ModalImage = styled.img`
  width: 100%;
  height: 300px;
  object-fit: cover;
  border-radius: 8px;
  margin-bottom: 2rem;
`;

const Avions = () => {
  const [filter, setFilter] = useState('all');
  const [selectedAircraft, setSelectedAircraft] = useState(null);

  const aircraft = [
    {
      id: 1,
      name: "Dassault Mirage III",
      period: "1961 - 1988",
      image: "/images/mirage3.jpg",
      category: "intercepteur",
      specs: {
        vitesse: "Mach 2.2",
        plafond: "17 000 m",
        autonomie: "2400 km",
        armement: "2 canons de 30mm, missiles"
      },
      description: "Le Mirage III fut le premier avion de combat français capable d'atteindre Mach 2 en vol horizontal. Il a servi comme intercepteur principal de l'Armée de l'Air pendant près de trois décennies."
    },
    {
      id: 2,
      name: "Dassault Mirage 2000",
      period: "1984 - 2020",
      image: "/images/mirage2000.jpg",
      category: "polyvalent",
      specs: {
        vitesse: "Mach 2.2+",
        plafond: "19 800 m",
        autonomie: "3300 km",
        armement: "Canon 30mm, missiles air-air et air-sol"
      },
      description: "Le Mirage 2000 représente une évolution majeure dans la famille des Mirage. Polyvalent et fiable, il a excellé dans les missions de défense aérienne et d'attaque au sol."
    },
    {
      id: 3,
      name: "Dassault Rafale",
      period: "2002 - 2023",
      image: "/images/rafale.jpg",
      category: "polyvalent",
      specs: {
        vitesse: "Mach 1.8+",
        plafond: "15 235 m",
        autonomie: "3700 km",
        armement: "Canon 30mm, large gamme de missiles et bombes"
      },
      description: "Fleuron de l'aviation militaire française, le Rafale est un chasseur omnirôle de dernière génération. Sa polyvalence et ses capacités en font un des meilleurs avions de combat au monde."
    }
  ];

  const filteredAircraft = filter === 'all' 
    ? aircraft 
    : aircraft.filter(a => a.category === filter);

  return (
    <>
      <Hero 
        title="Notre Flotte" 
        subtitle="L'excellence technologique au service de la défense aérienne"
        bgImage="/images/fleet-hero.jpg"
      />

      <Section 
        title="Nos Avions de Combat" 
        bgColor="#f5f5f5"
        pattern={true}
      >
        <FilterContainer>
          <FilterButton 
            active={filter === 'all'} 
            onClick={() => setFilter('all')}
          >
            Tous
          </FilterButton>
          <FilterButton 
            active={filter === 'intercepteur'} 
            onClick={() => setFilter('intercepteur')}
          >
            Intercepteurs
          </FilterButton>
          <FilterButton 
            active={filter === 'polyvalent'} 
            onClick={() => setFilter('polyvalent')}
          >
            Polyvalents
          </FilterButton>
        </FilterContainer>

        <AircraftGrid>
          <AnimatePresence>
            {filteredAircraft.map((aircraft) => (
              <AircraftCard
                key={aircraft.id}
                layout
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 50 }}
                transition={{ duration: 0.5 }}
                onClick={() => setSelectedAircraft(aircraft)}
              >
                <AircraftImage>
                  <img src={aircraft.image} alt={aircraft.name} />
                </AircraftImage>
                <AircraftInfo>
                  <AircraftName>{aircraft.name}</AircraftName>
                  <AircraftPeriod>{aircraft.period}</AircraftPeriod>
                  <SpecsList>
                    <SpecItem>
                      <span>Vitesse max</span>
                      <span>{aircraft.specs.vitesse}</span>
                    </SpecItem>
                    <SpecItem>
                      <span>Plafond</span>
                      <span>{aircraft.specs.plafond}</span>
                    </SpecItem>
                  </SpecsList>
                </AircraftInfo>
              </AircraftCard>
            ))}
          </AnimatePresence>
        </AircraftGrid>
      </Section>

      <AnimatePresence>
        {selectedAircraft && (
          <Modal
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedAircraft(null)}
          >
            <ModalContent
              onClick={e => e.stopPropagation()}
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
            >
              <CloseButton onClick={() => setSelectedAircraft(null)}>✕</CloseButton>
              <ModalImage src={selectedAircraft.image} alt={selectedAircraft.name} />
              <h2>{selectedAircraft.name}</h2>
              <p style={{ color: 'var(--gray-color)', marginBottom: '1.5rem' }}>
                {selectedAircraft.period}
              </p>
              <p style={{ marginBottom: '2rem', lineHeight: '1.6' }}>
                {selectedAircraft.description}
              </p>
              <h3 style={{ marginBottom: '1rem' }}>Spécifications techniques</h3>
              <SpecsList>
                {Object.entries(selectedAircraft.specs).map(([key, value]) => (
                  <SpecItem key={key}>
                    <span style={{ textTransform: 'capitalize' }}>{key}</span>
                    <span>{value}</span>
                  </SpecItem>
                ))}
              </SpecsList>
            </ModalContent>
          </Modal>
        )}
      </AnimatePresence>
    </>
  );
};

export default Avions;
