import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import Hero from '../components/Hero';
import Section from '../components/Section';

const PageBackground = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #1e3c72 0%, #2a5298 100%);
  padding: 2rem;
  position: relative;
  overflow-x: hidden;

  &::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 50% 50%, rgba(30, 60, 114, 0.2) 0%, rgba(42, 82, 152, 0.8) 100%);
    pointer-events: none;
    z-index: 0;
  }
`;

const AircraftContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 3rem;
  padding: 2rem;
  position: relative;
  z-index: 1;
  perspective: 2000px;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    padding: 1rem;
  }
`;

const StyledAircraftCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 20px;
  padding: 2rem;
  cursor: pointer;
  transform-style: preserve-3d;
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 15px 35px rgba(0, 0, 0, 0.2);

  &:hover {
    border-color: rgba(255, 255, 255, 0.4);
  }
`;

const AircraftImage = styled.div`
  width: 100%;
  height: 250px;
  overflow: hidden;
  border-radius: 15px;
  margin-bottom: 1.5rem;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.6s cubic-bezier(0.23, 1, 0.32, 1);
  }

  &:hover img {
    transform: scale(1.05);
  }
`;

const AircraftTitle = styled.h3`
  color: white;
  font-size: 1.5rem;
  margin: 1rem 0;
  font-weight: 600;
`;

const AircraftPeriod = styled.p`
  color: rgba(255, 255, 255, 0.8);
  font-size: 1rem;
  margin-bottom: 1rem;
`;

const SpecsList = styled.div`
  display: grid;
  gap: 0.75rem;
  margin-top: 1.5rem;
`;

const SpecItem = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 0.75rem 1rem;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 8px;

  strong {
    color: rgba(255, 255, 255, 0.9);
  }

  span {
    color: rgba(255, 255, 255, 0.7);
  }
`;

const Modal = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  padding: 2rem;
`;

const ModalContent = styled(motion.div)`
  background: white;
  padding: 2rem;
  border-radius: 10px;
  max-width: 800px;
  width: 90%;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: var(--primary-color);
`;

const ModalImage = styled.img`
  width: 100%;
  height: 300px;
  object-fit: cover;
  border-radius: 8px;
  margin-bottom: 2rem;
`;

const Avions = () => {
  const [selectedAircraft, setSelectedAircraft] = useState(null);
  const [blips, setBlips] = useState([]);
  const [filter, setFilter] = useState('all');

  const aircraft = [
    {
      id: 1,
      name: 'Mirage 2000C',
      image: '/images/M2000C.jpg',
      period: '1984 - Present',
      description: 'Avion de chasse monoplace, le Mirage 2000C est la version intercepteur du Mirage 2000.',
      category: 'intercepteur',
      specs: {
        'Longueur': '14.36 m',
        'Envergure': '9.13 m',
        'Hauteur': '5.20 m',
        'Masse maximale': '17.5 tonnes',
        'Vitesse max': 'Mach 2.2',
        'Plafond': '59,000 ft',
        'Motorisation': 'SNECMA M53-P2',
        'Armement': 'Canon DEFA 554, missiles Magic II et Super 530D'
      }
    },
    {
      id: 2,
      name: 'Mirage 2000B',
      image: '/images/M2000B.jpg',
      period: '1983 - Present',
      description: 'Version biplace d\'entraînement du Mirage 2000, conservant toutes les capacités opérationnelles.',
      category: 'entrainement',
      specs: {
        'Longueur': '14.36 m',
        'Envergure': '9.13 m',
        'Hauteur': '5.20 m',
        'Masse maximale': '17.5 tonnes',
        'Vitesse max': 'Mach 2.2',
        'Plafond': '59,000 ft',
        'Motorisation': 'SNECMA M53-P2',
        'Configuration': 'Double commande complète'
      }
    }
  ];

  useEffect(() => {
    const createBlip = () => {
      const x = Math.random() * window.innerWidth;
      const y = Math.random() * window.innerHeight;
      const newBlip = { id: Date.now(), x, y };
      setBlips(prev => [...prev, newBlip]);
      setTimeout(() => {
        setBlips(prev => prev.filter(blip => blip.id !== newBlip.id));
      }, 2000);
    };

    const interval = setInterval(createBlip, 500);
    return () => clearInterval(interval);
  }, []);

  const filteredAircraft = filter === 'all' 
    ? aircraft 
    : aircraft.filter(aircraft => aircraft.category === filter);

  return (
    <PageBackground>
      <Hero title="Nos Avions" />
      <Section>
        <AircraftContainer>
          <motion.div
            className="aircraft-scene"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1 }}
          >
            <AnimatePresence>
              {filteredAircraft.map(aircraft => (
                <motion.div
                  key={aircraft.id}
                  className="aircraft-wrapper"
                  initial={{ opacity: 0, rotateY: -90, z: -100 }}
                  animate={{
                    opacity: 1,
                    rotateY: 0,
                    z: 0
                  }}
                  exit={{ opacity: 0, rotateY: 90, z: -100 }}
                  transition={{
                    duration: 0.8,
                    ease: 'easeInOut'
                  }}
                >
                  <StyledAircraftCard
                    onClick={() => setSelectedAircraft(aircraft)}
                    whileHover={{
                      scale: 1.05,
                      rotateY: 15,
                      translateZ: 20
                    }}
                    transition={{
                      duration: 0.3,
                      ease: 'easeOut'
                    }}
                  >
                    <AircraftImage>
                      <img src={aircraft.image} alt={aircraft.name} />
                    </AircraftImage>
                    <AircraftTitle>{aircraft.name}</AircraftTitle>
                    <AircraftPeriod>{aircraft.period}</AircraftPeriod>
                    <SpecsList>
                      {Object.entries(aircraft.specs).slice(0, 3).map(([key, value], index) => (
                        <SpecItem key={index}>
                          <strong>{key}:</strong>
                          <span>{value}</span>
                        </SpecItem>
                      ))}
                    </SpecsList>
                  </StyledAircraftCard>
                </motion.div>
              ))}
            </AnimatePresence>
          </motion.div>
        </AircraftContainer>

        <AnimatePresence>
          {selectedAircraft && (
            <Modal
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setSelectedAircraft(null)}
            >
              <ModalContent
                onClick={e => e.stopPropagation()}
                initial={{ scale: 0.8, y: 50 }}
                animate={{ scale: 1, y: 0 }}
                exit={{ scale: 0.8, y: 50 }}
                transition={{ type: 'spring', damping: 25 }}
              >
                <CloseButton onClick={() => setSelectedAircraft(null)}>&times;</CloseButton>
                <ModalImage src={selectedAircraft.image} alt={selectedAircraft.name} />
                <h2>{selectedAircraft.name}</h2>
                <p>{selectedAircraft.description}</p>
                <SpecsList>
                  {Object.entries(selectedAircraft.specs).map(([key, value], index) => (
                    <SpecItem key={index}>
                      <strong>{key}</strong>
                      <span>{value}</span>
                    </SpecItem>
                  ))}
                </SpecsList>
              </ModalContent>
            </Modal>
          )}
        </AnimatePresence>
      </Section>
    </PageBackground>
  );
};

export default Avions;
