import React from 'react';
import styled from 'styled-components';
import Hero from '../components/Hero';
import Section from '../components/Section';

const AircraftGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-top: 3rem;
`;

const AircraftCard = styled.div`
  background: var(--card-bg);
  border-radius: var(--border-radius);
  padding: 2rem;
  box-shadow: var(--shadow-sm);
`;

const AircraftImage = styled.div`
  width: 100%;
  height: 200px;
  border-radius: var(--border-radius);
  overflow: hidden;
  margin-bottom: 1.5rem;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const SpecsList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 1rem 0;
`;

const SpecItem = styled.li`
  padding: 0.5rem 0;
  border-bottom: 1px solid var(--border-color);

  &:last-child {
    border-bottom: none;
  }
`;

const aircraftData = [
  {
    id: 1,
    name: 'Mirage 2000C',
    image: '/images/mirage2000C.jpg',
    specs: [
      'Vitesse max: Mach 2.2',
      'Plafond: 59,000 ft',
      'Motorisation: SNECMA M53-P2'
    ]
  },
  {
    id: 2,
    name: 'Mirage 2000B',
    image: '/images/mirage2000B.jpg',
    specs: [
      'Vitesse max: Mach 2.2',
      'Configuration: Double commande'
    ]
  }
];

const Avions = () => (
  <>
    <Hero title="Nos Avions" />
    <Section>
      <AircraftGrid>
        {aircraftData.map(aircraft => (
          <AircraftCard key={aircraft.id}>
            <AircraftImage>
              <img src={aircraft.image} alt={aircraft.name} />
            </AircraftImage>
            <h3>{aircraft.name}</h3>
            <SpecsList>
              {aircraft.specs.map((spec, index) => (
                <SpecItem key={index}>{spec}</SpecItem>
              ))}
            </SpecsList>
          </AircraftCard>
        ))}
      </AircraftGrid>
    </Section>
  </>
);

export default Avions;
