import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import Hero from '../components/Hero';
import Section from '../components/Section';

const GalleryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 2rem;
`;

const GalleryItem = styled(motion.div)`
  position: relative;
  height: 300px;
  cursor: pointer;
  overflow: hidden;
  border-radius: 10px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);

  &:hover img {
    transform: scale(1.1);
  }

  &:hover .overlay {
    opacity: 1;
  }
`;

const GalleryImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.5s ease;
`;

const GalleryOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(to top, rgba(0,0,0,0.8), rgba(0,0,0,0.3));
  opacity: 0;
  transition: opacity 0.3s ease;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  padding: 1.5rem;
  color: white;
`;

const CollectionGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
`;

const CollectionCard = styled(motion.div)`
  background: white;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
  }
`;

const CollectionImage = styled.div`
  height: 200px;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.5s ease;
  }

  ${CollectionCard}:hover img {
    transform: scale(1.1);
  }
`;

const CollectionInfo = styled.div`
  padding: 1.5rem;
`;

const CollectionTitle = styled.h3`
  color: var(--primary-color);
  margin-bottom: 0.5rem;
`;

const CollectionDescription = styled.p`
  color: var(--gray-color);
  line-height: 1.6;
`;

const Modal = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.9);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  padding: 2rem;
`;

const ModalContent = styled(motion.div)`
  max-width: 900px;
  width: 90%;
  background: white;
  border-radius: 10px;
  overflow: hidden;
  position: relative;
`;

const ModalImage = styled.img`
  width: 100%;
  height: auto;
  max-height: 80vh;
  object-fit: contain;
`;

const ModalInfo = styled.div`
  padding: 2rem;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: white;
  border: none;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 1.5rem;
  z-index: 10;
`;

const Musee = () => {
  const [selectedItem, setSelectedItem] = useState(null);

  const collections = [
    {
      title: "Uniformes et Équipements",
      description: "Collection d'uniformes et d'équipements de vol à travers les époques.",
      image: "/images/collection-uniformes.jpg"
    },
    {
      title: "Maquettes d'Avions",
      description: "Reproduction fidèle des appareils ayant servi dans l'escadron.",
      image: "/images/collection-maquettes.jpg"
    },
    {
      title: "Documents Historiques",
      description: "Archives, photographies et documents retraçant l'histoire de l'unité.",
      image: "/images/collection-documents.jpg"
    },
    {
      title: "Médailles et Décorations",
      description: "Distinctions reçues par les membres de l'escadron au fil des ans.",
      image: "/images/collection-medailles.jpg"
    }
  ];

  const gallery = [
    {
      id: 1,
      title: "Cockpit du Mirage III",
      description: "Vue intérieure restaurée du cockpit d'un Mirage III",
      image: "/images/musee-cockpit.jpg"
    },
    {
      id: 2,
      title: "Salle des Traditions",
      description: "Exposition permanente sur l'histoire de l'escadron",
      image: "/images/musee-traditions.jpg"
    },
    {
      id: 3,
      title: "Mémorial",
      description: "Hommage aux aviateurs de l'escadron",
      image: "/images/musee-memorial.jpg"
    },
    {
      id: 4,
      title: "Hangar Historique",
      description: "Conservation des appareils historiques",
      image: "/images/musee-hangar.jpg"
    },
    {
      id: 5,
      title: "Galerie des Commandants",
      description: "Portraits des commandants successifs",
      image: "/images/musee-commandants.jpg"
    },
    {
      id: 6,
      title: "Salle des Missions",
      description: "Exposition sur les missions marquantes",
      image: "/images/musee-missions.jpg"
    }
  ];

  return (
    <>
      <Hero 
        title="Notre Musée" 
        subtitle="Un voyage dans l'histoire de l'aviation militaire française"
        bgImage="/images/musee-hero.jpg"
      />

      <Section 
        title="Collections Permanentes" 
        bgColor="#f5f5f5"
        pattern={true}
      >
        <p style={{ 
          textAlign: 'center', 
          maxWidth: '800px', 
          margin: '0 auto 3rem auto', 
          lineHeight: '1.8', 
          fontSize: '1.1rem' 
        }}>
          Notre musée préserve et met en valeur le riche patrimoine historique de 
          l'escadron à travers diverses collections thématiques.
        </p>

        <CollectionGrid>
          {collections.map((collection, index) => (
            <CollectionCard
              key={index}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
            >
              <CollectionImage>
                <img src={collection.image} alt={collection.title} />
              </CollectionImage>
              <CollectionInfo>
                <CollectionTitle>{collection.title}</CollectionTitle>
                <CollectionDescription>{collection.description}</CollectionDescription>
              </CollectionInfo>
            </CollectionCard>
          ))}
        </CollectionGrid>
      </Section>

      <Section 
        title="Galerie" 
        bgColor="white"
      >
        <GalleryGrid>
          {gallery.map((item, index) => (
            <GalleryItem
              key={item.id}
              initial={{ opacity: 0, scale: 0.8 }}
              whileInView={{ opacity: 1, scale: 1 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              onClick={() => setSelectedItem(item)}
            >
              <GalleryImage src={item.image} alt={item.title} />
              <GalleryOverlay className="overlay">
                <h3 style={{ marginBottom: '0.5rem' }}>{item.title}</h3>
                <p>{item.description}</p>
              </GalleryOverlay>
            </GalleryItem>
          ))}
        </GalleryGrid>
      </Section>

      <Section 
        title="Informations Pratiques" 
        bgColor="var(--primary-color)"
        titleColor="white"
      >
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
          gap: '2rem',
          color: 'white'
        }}>
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
          >
            <h3 style={{ color: 'var(--secondary-color)', marginBottom: '1rem' }}>
              Horaires
            </h3>
            <p style={{ lineHeight: '1.8' }}>
              Mardi - Dimanche<br />
              10h00 - 18h00<br />
              Fermé les lundis et jours fériés
            </p>
          </motion.div>

          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ delay: 0.1 }}
          >
            <h3 style={{ color: 'var(--secondary-color)', marginBottom: '1rem' }}>
              Tarifs
            </h3>
            <p style={{ lineHeight: '1.8' }}>
              Adultes : 8€<br />
              Enfants (6-12 ans) : 4€<br />
              Militaires : Gratuit
            </p>
          </motion.div>

          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ delay: 0.2 }}
          >
            <h3 style={{ color: 'var(--secondary-color)', marginBottom: '1rem' }}>
              Visites Guidées
            </h3>
            <p style={{ lineHeight: '1.8' }}>
              Sur réservation<br />
              Groupes de 10 personnes maximum<br />
              Durée : 1h30
            </p>
          </motion.div>
        </div>
      </Section>

      <AnimatePresence>
        {selectedItem && (
          <Modal
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedItem(null)}
          >
            <ModalContent
              onClick={e => e.stopPropagation()}
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
            >
              <CloseButton onClick={() => setSelectedItem(null)}>✕</CloseButton>
              <ModalImage src={selectedItem.image} alt={selectedItem.title} />
              <ModalInfo>
                <h2>{selectedItem.title}</h2>
                <p style={{ color: 'var(--gray-color)', marginTop: '1rem', lineHeight: '1.6' }}>
                  {selectedItem.description}
                </p>
              </ModalInfo>
            </ModalContent>
          </Modal>
        )}
      </AnimatePresence>
    </>
  );
};

export default Musee;
