import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import Hero from '../components/Hero';
import Section from '../components/Section';

const ContentWrapper = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
`;

const GalleryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 2rem;
`;

const GalleryItem = styled(motion.div)`
  position: relative;
  height: 300px;
  cursor: pointer;
  overflow: hidden;
  border-radius: 10px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);

  &:hover img {
    transform: scale(1.1);
  }

  &:hover .overlay {
    opacity: 1;
  }
`;

const GalleryImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.5s ease;
`;

const GalleryOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(to top, rgba(0,0,0,0.8), rgba(0,0,0,0.3));
  opacity: 0;
  transition: opacity 0.3s ease;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  padding: 1.5rem;
  color: white;
`;

const CollectionGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
`;

const CollectionCard = styled(motion.div)`
  background: white;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
  }
`;

const CollectionImage = styled.div`
  height: 200px;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.5s ease;
  }

  ${CollectionCard}:hover img {
    transform: scale(1.1);
  }
`;

const CollectionInfo = styled.div`
  padding: 1.5rem;
`;

const CollectionTitle = styled.h3`
  color: var(--primary-color);
  margin-bottom: 0.5rem;
`;

const CollectionDescription = styled.p`
  color: var(--gray-color);
  line-height: 1.6;
`;

const Modal = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.9);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  padding: 2rem;
`;

const ModalContent = styled(motion.div)`
  max-width: 900px;
  width: 90%;
  background: white;
  border-radius: 10px;
  overflow: hidden;
  position: relative;
`;

const ModalImage = styled.img`
  width: 100%;
  height: auto;
  max-height: 80vh;
  object-fit: contain;
`;

const ModalInfo = styled.div`
  padding: 2rem;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: white;
  border: none;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 1.5rem;
  z-index: 10;
`;

const InfoSection = styled.div`
  background: white;
  border-radius: 10px;
  padding: 2rem;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  margin-bottom: 3rem;
`;

const InfoTitle = styled.h3`
  color: var(--primary-color);
  margin-bottom: 1.5rem;
  font-size: 1.5rem;
`;

const InfoContent = styled.div`
  color: var(--gray-color);
  line-height: 1.8;

  p {
    margin-bottom: 1rem;
  }

  strong {
    color: var(--primary-color);
  }

  a {
    color: var(--secondary-color);
    text-decoration: none;

    &:hover {
      text-decoration: underline;
    }
  }
`;

const HighlightsList = styled.ul`
  list-style: none;
  padding: 0;
  margin-top: 1rem;

  li {
    padding-left: 1.5rem;
    position: relative;
    margin-bottom: 0.5rem;

    &:before {
      content: '→';
      position: absolute;
      left: 0;
      color: var(--secondary-color);
    }
  }
`;

const Musee = () => {
  const [selectedItem, setSelectedItem] = useState(null);

  const collections = [
    {
      title: "Cockpit du Mirage III",
      description: "Vue intérieure restaurée du cockpit d'un Mirage III",
      image: "/images/musée1.jpg"
    },
    {
      title: "Maquettes d'Avions",
      description: "Reproduction fidèle des appareils ayant servi dans l'escadron.",
      image: "/images/collection-maquettes.jpg"
    },
    {
      title: "Documents Historiques",
      description: "Archives, photographies et documents retraçant l'histoire de l'unité.",
      image: "/images/collection-documents.jpg"
    },
    {
      title: "Médailles et Décorations",
      description: "Distinctions reçues par les membres de l'escadron au fil des ans.",
      image: "/images/collection-medailles.jpg"
    }
  ];

  const gallery = [
    {
      id: 1,
      title: "Cockpit du Mirage III",
      description: "Vue intérieure restaurée du cockpit d'un Mirage III",
      image: "/images/musée1.jpg"
    },
    {
