            viewport={{ once: true }}
            transition={{ duration: 0.5, delay: 0.1 }}
          >
            <StatNumber>12K+</StatNumber>
            <StatTitle>Heures de vol</StatTitle>
          </StatItem>

          <StatItem
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            <StatNumber>150+</StatNumber>
            <StatTitle>Pilotes form√©s</StatTitle>
          </StatItem>

          <StatItem
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5, delay: 0.3 }}
          >
            <StatNumber>25+</StatNumber>
            <StatTitle>Missions internationales</StatTitle>
          </StatItem>
        </StatsContainer>
      </Hero>

      <Section 
        title="Notre H√©ritage" 
        bgColor="#f5f5f5"
        pattern={true}
