import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import Hero from '../components/Hero';
import Section from '../components/Section';

const FilterContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-bottom: 3rem;
  flex-wrap: wrap;
`;

const FilterButton = styled.button`
  background: ${props => props.active ? 'var(--primary-color)' : 'transparent'};
  color: ${props => props.active ? 'white' : 'var(--primary-color)'};
  border: 2px solid var(--primary-color);
  padding: 0.5rem 1.5rem;
  border-radius: 25px;
  font-weight: 600;
  cursor: pointer;
  transition: var(--transition);

  &:hover {
    background: var(--primary-color);
    color: white;
  }
`;

const AircraftGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
`;

const AircraftCard = styled(motion.div)`
  background: white;
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  cursor: pointer;
  position: relative;

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(45deg, rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.05));
    z-index: 1;
    transition: opacity 0.3s ease;
    opacity: 0;
  }

  &:hover::before {
    opacity: 1;
  }
`;

const AircraftImage = styled.div`
  height: 200px;
  position: relative;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.5s ease;
  }

  ${AircraftCard}:hover img {
    transform: scale(1.1);
  }
`;

const AircraftInfo = styled.div`
  padding: 1.5rem;
  position: relative;
  z-index: 2;
`;

const AircraftName = styled.h3`
  color: var(--primary-color);
  margin-bottom: 0.5rem;
  font-size: 1.5rem;
  text-transform: uppercase;
  letter-spacing: 1px;
`;

const AircraftPeriod = styled.p`
  color: var(--gray-color);
  font-size: 0.9rem;
  margin-bottom: 1rem;
`;

const SpecsList = styled.ul`
  list-style: none;
  padding: 0;
`;

const SpecItem = styled.li`
  display: flex;
  justify-content: space-between;
  padding: 0.5rem 0;
  border-bottom: 1px solid #eee;

  &:last-child {
    border-bottom: none;
  }
`;

const Modal = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
  padding: 2rem;
`;

const ModalContent = styled(motion.div)`
  background: white;
  padding: 2rem;
  border-radius: 10px;
  max-width: 800px;
  width: 90%;
  max-height: 90vh;
  overflow-y: auto;
  position: relative;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: var(--primary-color);
`;

const ModalImage = styled.img`
  width: 100%;
  height: 300px;
  object-fit: cover;
  border-radius: 8px;
  margin-bottom: 2rem;
`;

const Avions = () => {
  const [blips, setBlips] = useState([]);
  const [filter, setFilter] = useState('all');
  const [selectedAircraft, setSelectedAircraft] = useState(null);

  useEffect(() => {
    const createBlip = () => {
      const x = Math.random() * window.innerWidth;
      const y = Math.random() * window.innerHeight;
      const newBlip = { id: Date.now(), x, y };
      setBlips(prev => [...prev, newBlip]);
      setTimeout(() => {
        setBlips(prev => prev.filter(blip => blip.id !== newBlip.id));
      }, 2000);
    };

    const interval = setInterval(createBlip, 500);
    return () => clearInterval(interval);
  }, []);

  const aircraft = [
    {
      id: 1,
      name: 'Mirage 2000C',
      image: '/images/M2000C.jpg',
      description: 'Avion de chasse monoplace, le Mirage 2000C est la version intercepteur du Mirage 2000.',
      specs: [
        'Vitesse max: Mach 2.2',
        'Plafond: 59,000 ft',
        'Motorisation: SNECMA M53-P2',
        'Armement: Canon DEFA 554, missiles Magic II et Super 530D'
      ]
    },
    {
      id: 2,
      name: 'Mirage 2000B',
      image: '/images/M2000B.jpg',
      description: 'Version biplace d\'entraînement du Mirage 2000, conservant toutes les capacités opérationnelles.',
      specs: [
        'Vitesse max: Mach 2.2',
        'Plafond: 59,000 ft',
        'Motorisation: SNECMA M53-P2',
        'Double commande complète'
      ]
    },
    {
      id: 3,
      name: "Dassault Mirage III",
      period: "1961 - 1988",
      image: "/images/mirage3.jpg",
      category: "intercepteur",
      specs: {
        vitesse: "Mach 2.2",
        plafond: "17 000 m",
        autonomie: "2400 km",
        armement: "2 canons de 30mm, missiles"
      }
    }
  ];

  const filteredAircraft = filter === 'all' 
    ? aircraft 
    : aircraft.filter(aircraft => aircraft.category === filter);

  return (
    <>
      <Hero title="Nos Avions" />
      <Section>
        <FilterContainer>
          <FilterButton active={filter === 'all'} onClick={() => setFilter('all')}>Tous</FilterButton>
          <FilterButton active={filter === 'intercepteur'} onClick={() => setFilter('intercepteur')}>Intercepteurs</FilterButton>
          <FilterButton active={filter === 'entrainement'} onClick={() => setFilter('entrainement')}>Entraînement</FilterButton>
        </FilterContainer>
        <AircraftGrid>
          <AnimatePresence>
            {filteredAircraft.map(aircraft => (
              <AircraftCard key={aircraft.id} layout initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} onClick={() => setSelectedAircraft(aircraft)}>
                <AircraftImage>
                  <img src={aircraft.image} alt={aircraft.name} />
                </AircraftImage>
                <AircraftInfo>
                  <AircraftName>{aircraft.name}</AircraftName>
                  <AircraftPeriod>{aircraft.period}</AircraftPeriod>
                  <SpecsList>
                    {Object.entries(aircraft.specs).map(([key, value]) => (
                      <SpecItem key={key}>
                        <span>{key}</span>
                        <span>{value}</span>
                      </SpecItem>
                    ))}
                  </SpecsList>
                </AircraftInfo>
              </AircraftCard>
            ))}
          </AnimatePresence>
        </AircraftGrid>
      </Section>
      <AnimatePresence>
        {selectedAircraft && (
          <Modal initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} onClick={() => setSelectedAircraft(null)}>
            <ModalContent onClick={e => e.stopPropagation()}>
              <CloseButton onClick={() => setSelectedAircraft(null)}>×</CloseButton>
              <ModalImage src={selectedAircraft.image} alt={selectedAircraft.name} />
              <h3>{selectedAircraft.name}</h3>
              <p>{selectedAircraft.description}</p>
              <SpecsList>
                {Object.entries(selectedAircraft.specs).map(([key, value]) => (
                  <SpecItem key={key}>
                    <span>{key}</span>
                    <span>{value}</span>
                  </SpecItem>
                ))}
              </SpecsList>
            </ModalContent>
          </Modal>
        )}
      </AnimatePresence>
    </>
  );
};

export default Avions;
